import sqlite3def dorm_matcher(wish):    connection = sqlite3.connect("studentDataBase.db")    cursor = connection.cursor()    cursor.execute('''SELECT UNIVERSITY_NAME, ROOM_AVAILABILITY FROM University''')                                           result = cursor.fetchall()        result_list = []    user_wish = False        if wish == "Да":        user_wish = True    else:        user_wish = False            for item in result:        if item[1] == 0 and user_wish:            result_list.append((item[0], 0))        else:            result_list.append((item[0], 1))    return result_list        